Properties.Octopus.Action.Aws.CloudFormationTemplat:
{
  "AWSTemplateFormatVersion": "2010-09-09",
  "Description": "POC3 Node.js/React ECS Service",
  "Parameters": {
    "ClusterName": {
      "Type": "String",
      "Default": "#{ECS.ClusterName}"
    },
    "ServiceName": {
      "Type": "String",
      "Default": "#{NodeJS.ServiceName}"
    },
    "ImageUri": {
      "Type": "String",
      "Default": "#{ECR.Registry}/poc3-nodejs-react:#{Octopus.Release.Number}"
    },
    "TargetGroupArn": {
      "Type": "String",
      "Default": "#{LoadBalancer.TargetGroup.NodeJS}"
    }
  },
  "Resources": {
    "TaskDefinition": {
      "Type": "AWS::ECS::TaskDefinition",
      "Properties": {
        "Family": "poc3-nodejs-react",
        "NetworkMode": "bridge",
        "RequiresCompatibilities": ["EC2"],
        "ExecutionRoleArn": "#{AWS.ECS.ExecutionRole}",
        "TaskRoleArn": "#{AWS.ECS.TaskRole}",
        "ContainerDefinitions": [
          {
            "Name": "poc3-nodejs-react",
            "Image": "#{ImageUri}",
            "Memory": 512,
            "PortMappings": [
              {
                "ContainerPort": 3000,
                "HostPort": 0,
                "Protocol": "tcp"
              }
            ],
            "Environment": [
              {
                "Name": "NODE_ENV",
                "Value": "#{Environment.Type}"
              },
              {
                "Name": "PORT",
                "Value": "3000"
              }
            ],
            "LogConfiguration": {
              "LogDriver": "awslogs",
              "Options": {
                "awslogs-group": "/ecs/poc3-nodejs-react",
                "awslogs-region": "#{AWS.Region}",
                "awslogs-stream-prefix": "ecs"
              }
            },
            "HealthCheck": {
              "Command": ["CMD-SHELL", "curl -f http://localhost:3000/health || exit 1"],
              "Interval": 30,
              "Timeout": 5,
              "Retries": 3,
              "StartPeriod": 60
            },
            "Essential": true
          }
        ]
      }
    },
    "Service": {
      "Type": "AWS::ECS::Service",
      "Properties": {
        "Cluster": "#{ClusterName}",
        "ServiceName": "#{ServiceName}",
        "TaskDefinition": {"Ref": "TaskDefinition"},
        "DesiredCount": 2,
        "LaunchType": "EC2",
        "LoadBalancers": [
          {
            "ContainerName": "poc3-nodejs-react",
            "ContainerPort": 3000,
            "TargetGroupArn": "#{TargetGroupArn}"
          }
        ],
        "DeploymentConfiguration": {
          "MaximumPercent": 200,
          "MinimumHealthyPercent": 50
        },
        "PlacementStrategy": [
          {
            "Type": "spread",
            "Field": "attribute:ecs.availability-zone"
          },
          {
            "Type": "spread",
            "Field": "instanceId"
          }
        ]
      }
    }
  }
}

